import React from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

const CarouselPip = ({
    children,
    // currentSlideMessage,
    isCurrentPip,
    // slideMessage,
    // slideNumber
}) => {
    const pipClasses = classNames('pw-carousel__pip', {
        'pw--active': isCurrentPip
    })

    return (
        <div className={pipClasses}>
            <span className="u-visually-hidden">
                {children}
            </span>
        </div>
    )
}

CarouselPip.defaultProps = {
    currentSlideMessage: 'Current slide',
    slideMessage: 'Slide',
}

CarouselPip.propTypes = {
    children: PropTypes.oneOfType([
        PropTypes.string,
        PropTypes.node
    ]),
    isCurrentPip: PropTypes.bool
}

CarouselPip.displayName = 'CarouselPip'

export default CarouselPip
