import React from 'react'
import PropTypes from 'prop-types'
import classNames from 'classnames'

/**
 * Related component:
 *
 * * [GridSpan](#!/GridSpan)
 *
 * `Grid` is a wrapper component for preparing grid layouts. It acts as the
 * initial foundation in which the GridSpan component may be used to
 * define layouts.
 *
 * Our grid framework of choice is [Susy](http://susydocs.oddbird.net/en/latest/)
 * and all of Grid's and GridSpan's behaviors are defined with the Susy mixins.
 *
 * @example ./DESIGN.md
 */

const Grid = ({
    children,
    className
}) => {
    const classes = classNames('pw-grid', className)

    return (
        <div className={classes}>
            <div className="pw-grid__inner">
                {children}
            </div>
        </div>
    )
}

Grid.propTypes = {
    /**
     * Any children to be nested within this component (most likely a GridSpan).
     */
    children: PropTypes.node,

    /**
     * Adds values to the `class` attribute of the root element
     */
    className: PropTypes.string,
}

export default Grid
