export const typecheck = (
    process.env.NODE_ENV === 'production'
    /* istanbul ignore if */
        ? /* istanbul ignore next */ (type, value) => value
        : (type, value) => {
            try {
                type.check(value)
            } catch (e) {
                console.error('Type check failed: ', e, '\n\nValue: ', value)
            }
            return value
        }
)
